import React, { useState, useEffect } from "react";
import firebase from "firebase/app";
import "./App.css";
import Iframe from "react-iframe";

const db = () => firebase.database();

function Dashboard() {
  const [reservas, handleReservas] = useState([]);
  const [item, setItem] = useState(0);

  useEffect(() => {
    db();
    console.log("Mounted ");
  });

  useEffect(() => {
    db()
      .ref("/")
      .on("value", handleReservas);
  });

  const deleteColletion = () => {
    firebase
      .database()
      .ref("/")
      .remove();
  };

  const logOut = () => {
    firebase
      .auth()
      .signOut()
      .then(
        function() {},
        function(error) {
          console.log(error);
        }
      );
  };

  const writeAdminData = userInfo => {
    firebase
      .database()
      .ref("/")
      .push({
        userInfo
      })
      .catch(error => {
        console.log("error ", error);
      });
  };

  const myData = JSON.stringify(reservas);
  const myDashboard = myData.split(",").map((item, i) =>
    item
      .replace(RegExp(/([.*+?^=!$(){}|[\]/\\""a-z])/g), " ")
      .replace("userInfo", "")
      .replace(":", "")
  );
  const product =
    "https://www.milanuncios.com/renovar/?id=" + myDashboard[item];
  const nextProduct = () => {
    setItem(item + 1);
  };
  return (
    <div>
      <p>{myDashboard[0]}</p>
      <p>{myDashboard[1]}</p>
      <button onClick={() => logOut()}>Log Out</button>
      <Iframe url="https://www.milanuncios.com"></Iframe>
      <Iframe url={product}></Iframe>
      <button onClick={() => nextProduct()}> Next </button>
    </div>
  );
}

export default Dashboard;
